import React, { Component } from 'react';

import Log from './Log';

import '../css/DynamicContentWidget.css';


class DynamicContentWidgetContext {

    constructor( id, label, css, text )
    {
        this.id = id;
        this.label = label;
        this.css = css;
        this.text = text;
    }
}

module.exports = DynamicContentWidgetContext;

export default class DynamicContentWidget extends Component {

    componentWillMount ()
    {
        console.log( this.props );

        if( this.props.context instanceof DynamicContentWidgetContext )
        {
                console.log("This can;t be true");
        }
        else
        {
            throw( new Event("Error: DynamicContentWidget requires context to be an instance of DynamicContentWidgetContext." ) );
        }
    }

    render() {
        return (
            <div></div>
        );
    }
}
